import React from 'react';
import { Link } from 'react-router-dom';
// import { useParams } from 'react-router';
// import axiosWithAuth from '../utils/axiosWithAuth';
import Products from './Products';


const SellersDashboard = (props) => {
  const userName = localStorage.getItem("name");
   const date = new Date();
    var hourFormat = date.getHours() === 0 ? 12 : date.getHours() % 12;
    var am_pm = date.getHours() < 12 ? "AM" : "PM";
    var minFormat = (date.getMinutes() % 60) < 10 ? "0" :"";
    var greeting =  date.getHours() >= 18 ? "Good evening, "
        : date.getHours() >= 12 ? "Good afternoon, "
        : "Good morning, ";
  return (
    <div>
      <div className="table-title">
          <div className="row">
          <div className="col-sm-6">
            <h2>Welcome, {userName}!</h2>
          </div>
        <div >
          <Link to="/sellers/add" className="btn"><i className="material-icons">&#xE147;</i> <span>Add New Product</span></Link>
          <Link to="/sellers" className="btn">View Added Products</Link>
          </div>
        </div>
      </div>
      	<Products products={props.products} addItem={props.addItem} />
    </div>
  )
}

export default SellersDashboard;
