import * as yup from 'yup';
const formSchema = yup.object().shape({
  username: yup.string().required('Username is required'),
  password: yup.string().required('Password is required')
});
export default formSchema;
export const itemFormSchema = yup.object().shape({
  title: yup.string().required('You must enter a title for the item').min(4, 'The item name must be at least 4 characters long'),
  image: yup.string().required('You must enter a brand name for the item'),
  description: yup.string().required('You must enter a description for the item').min(10, 'The description must be at least 10 characters long'),
  price: yup.number().required('You must enter a suggested rental price').min(1)
});
