import React, { useState } from 'react'
import axiosWithAuth from '../utils/axiosWithAuth';
import * as yup from 'yup'
import { signUpFormSchema } from '../utils/signUpFormSchema';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const initialFormValues = {
    userrole:{
        userroleid: 0,
        userroletype: "",
    },
    username: "",
    fname: "",
    lname: "",
    email: "",
    password: "",
  }

const initialFormErrors = { 
  userrole:{
        userroleid: 0,
        userroletype: "",
    },
    username: "",
    fname: "",
    lname: "",
    email: "",
    password: "",
}

export default function SignUpForm() {

  const [user, setUser] = useState(initialFormValues)
  const [formValues, setFormValues] = useState(initialFormValues)
  const [formErrors, setFormErrors] = useState(initialFormErrors)
  const { push } = useHistory();

  
  const onChange = (evt) => {
    const { name, value } = evt.target
    console.log(evt.target.name, evt.target.value)

    yup
      .reach(signUpFormSchema, name)
      .validate(value)
      .then(() => {
        setFormErrors({
          ...formErrors,
          [name]: "",
        });
      })
      .catch((err) => {
        setFormErrors({
          ...formErrors,
          [name]: err.errors[0],
        });
      });

    setFormValues({
      ...formValues, 
      [name]:
        //Check for checkboxes
        evt.target.type === 'checkbox' ? evt.target.checked :
          value,
    })
  }

  const signUpUser = (newUser) => {
    axios
      .post('https://usemytechstuff-tt26.herokuapp.com/users/user', newUser)
      .then(res => {
        console.log(res.data)
        setUser(res.data)
        setFormValues(initialFormValues)
        push('/login')
      })
      .catch(err => {
        console.log(err.response)
      })
    }
  const onSubmit = (evt) => {
    evt.preventDefault();
    
    const newUser = {
      email: formValues.email.trim(),
      password: formValues.password.trim(),
    }
    
    signUpUser(newUser)
    // // call to API

    // console.log(newUser)
  }
  console.log(user)
  return (
    <div>
      <form className="form container" onSubmit={onSubmit}>
      <div>
        <div className='errors'>  
                <div>{formErrors.fname}</div>
                <div>{formErrors.lname}</div>
                <div>{formErrors.username}</div>
                <div>{formErrors.password}</div>
                <div>{formErrors.userrole.type}</div>
          </div>
          <label className='form-row' >
                <input 
                  type="text"
                  onChange={onChange}
                  placeholder="First Name"
                  name="fname"
                  value={formValues.fname}
                />
              </label>
              <label className='form-row'>
                <input
                  type="text"
                  onChange={onChange}
                  placeholder="Last Name"
                  name="lname"
                  value={formValues.lname}
                />
              </label>
              <label className='form-row'>
                <input
                  type="text"
                  onChange={onChange}
                  placeholder="Username"
                  name="username"
                  value={formValues.username}
                  />
              </label>
              <label className='form-row'>
                <input
                  type="email"
                  onChange={onChange}
                  placeholder="E-mail"
                  name="email"
                  value={formValues.email}
                  />
              </label>
              <label className='form-row'>
                <input
                  type="password" 
                  onChange={onChange}
                  placeholder="Password"
                  name="password"
                  value={formValues.password}
                  />
              </label >
              
              <label className='form-row' >
                <p>Account Role </p>
                <select name="userrole" value={formValues.userrole} onChange={onChange}>
                  <option value="none">----Select----</option>
                  <option value={1}>OWNER</option>
                  <option value={2}>RENTER</option>
                </select>
              </label>
        </div>
        <div>
          <label>
            Password:
            <input
              value={formValues.password}
              onChange={onChange}
              name="password"
              type="password"
            />
            {formErrors.password}
          </label>
        </div>
        <div>
          <button id="submitBtn">Sign Up</button>
        </div>
        <div className='form-button'> 
            <button onClick={clickBack}>Back</button>
            </div>
      </form>
    </div>
  )

}
